import axios from "axios";
import { reactLocalStorage } from "reactjs-localstorage";
const { default: AuthHandler } = require("./AuthHandler");

const {default: Config} = require ("./config")


 
class APIHandler{
    async checkLogin(){
        if(AuthHandler.checkTokenExpiry()){
            try{
            var response = await axios.post(Config.refreshApiUrl,{
                refresh: AuthHandler.getRefreshToken()
            }) 
            reactLocalStorage.set("token",response.data.access) 
        }
        catch(error){
            console.log(error)
            //Not using a valid token then refresh and logout the user
            AuthHandler.logoutUser()
            window.location = '/'
        }
        }
        
    }
    async saveCompanyData(
        name,
        license_no,
        address,
        contact_no,
        email,
        description
        ){
        await this.checkLogin()
        //wait untill token get updated
        var response = await axios.post(
            Config.companyApiUrl,{
                name:name,
                license_no:license_no,
                address:address,
                contact_no:contact_no,
                email:email,
                description:description,
            },
        {headers:{Authorization: "Bearer " + AuthHandler.getLoginToken()}})
        return response;
    }
    async fetchAllCompany(){
        await this.checkLogin()

        var response = await axios.get(
            Config.companyApiUrl,
            {
                headers:{Authorization: "Bearer " + AuthHandler.getLoginToken()}
            })
            return response;
    }

    async fetchCompanyDetails(id){
        await this.checkLogin()

        var response = await axios.get(
            Config.companyApiUrl+""+id+"/",
            {
                headers:{Authorization: "Bearer " + AuthHandler.getLoginToken()}
            })
            return response;
    }

    async editCompanyData(
        name,
        license_no,
        address,
        contact_no,
        email,
        description,
        id
        ){
        await this.checkLogin()
        //wait untill token get updated
        var response = await axios.put(
            Config.companyApiUrl+""+id+'/',{
                name:name,
                license_no:license_no,
                address:address,
                contact_no:contact_no,
                email:email,
                description:description,
            },
        {headers:{Authorization: "Bearer " + AuthHandler.getLoginToken()}})
        return response;
    }
    async saveCompanyBankData(
        accounts_holder_name,
        bank_account_no,
        ifsc_Code,
        company_id
        
        ){
        await this.checkLogin()
        //wait untill token get updated
        var response = await axios.post(
            Config.companyBankApiUrl,{
                accounts_holder_name:accounts_holder_name,
                bank_account_no:bank_account_no,
                ifsc_Code:ifsc_Code,
                company_id:company_id
            },
        {headers:{Authorization: "Bearer " + AuthHandler.getLoginToken()}})
        return response;
    }
    async fetchCompanyBankDetails(id){
        await this.checkLogin()

        var response = await axios.get(
            Config.companyBankApiUrl+""+id+"/",
            {
                headers:{Authorization: "Bearer " + AuthHandler.getLoginToken()}
            })
            return response;
    }
    async editCompanyBankData(
        accounts_holder_name,
        bank_account_no,
        ifsc_Code,
        company_id,
        id
        ){
        await this.checkLogin()
        //wait untill token get updated
        var response = await axios.put(
            Config.companyBankApiUrl+""+id+'/',{
                accounts_holder_name:accounts_holder_name,
                bank_account_no:bank_account_no,
                ifsc_Code:ifsc_Code,
                company_id:company_id
            },
        {headers:{Authorization: "Bearer " + AuthHandler.getLoginToken()}})
        return response;
}

    async fetchAllCompanyOnly(){
    await this.checkLogin()

    var response = await axios.get(
        Config.companyOnlyUrl,
        {
            headers:{Authorization: "Bearer " + AuthHandler.getLoginToken()}
        })
        return response;
}

    async saveMedicineData( medicine_name,
        medical_type,
        buy_price,
        sell_price,
        c_gst,
        s_gst,
        batch_no,
        shelf_no,
        expire_date,
        mfg_date,
        company_id,
        description,
        in_stock_total,
        qty_in_strip,
        medicinedetails){
        await this.checkLogin()
        var response = await axios.post(
            Config.medicineApiUrl,{
                medicine_name:medicine_name,
                medical_type:medical_type,
                buy_price:buy_price,
                sell_price:sell_price,
                c_gst:c_gst,
                s_gst:s_gst,
                batch_no:batch_no,
                shelf_no:shelf_no,
                expire_date:expire_date,
                mfg_date:mfg_date,
                company_id: company_id,
                description:description,
                in_stock_total:in_stock_total,
                qty_in_strip:qty_in_strip,
                medicine_details:medicinedetails,
                
            },
        {headers:{Authorization: "Bearer " + AuthHandler.getLoginToken()}})
        return response;
       
    }

    async fetchAllMedicine(){
        await this.checkLogin()
    
        var response = await axios.put(
            Config.medicineApiUrl,
            {
                headers:{Authorization: "Bearer " + AuthHandler.getLoginToken()}
            })
            return response;
        }
